import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'
import GlobalFonts from '../fonts/Poppins/fonts'

const Title = styled.h1.attrs({
    className: 'h1',
})`
    align-self: center;
`


const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    padding: 0.5em;
    margin: 0.5em;
    border-radius: 1px;
`

const Button = styled.button`
    margin: 15px 15px 10px 5px;
    background: ${props => props.primary ? "white" : "#484848"};
    color: ${props => props.primary ? "#484848" : "white"};
    border: #484848;
    border-radius: 1px;
    padding: 10px 120px;
    transition-duration: 0.1s;

`
const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 10px 20px;
    font-family: 'Popins';
    color: #484848;
    display: flex;
    flex-direction: column;
    justify-items: center;
    width: 50%
`

const ButtonHover = styled.button`
    margin: 15px 15px 15px 5px;
    background: ${props => props.primary ? "white" : "#36B1A7"};
    color: ${props => props.primary ? "#36B1A7" : "white"};
    border: #36B1A7;
    border-radius: 1px;
    padding: 10px 120px;
    ${Button}:hover & {
        color: #484848;
      }
`
const InfoForm = styled.form`
    display: flex;
    flex-direction: column;
    justify-items: center;
`


class InfoInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            email: '',
        }
    }

    handleChangeInputName = event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputEmail = event => {
        const email = event.target.value
        this.setState({ email })
    }

    handleIncludeInfo = async () => {
        const { name, email } = this.state
        const payload = { name, email }

        await api.createInfo(payload).then(res => {
            window.alert(`Info inserted successfully`)
            this.setState({
                name: '',
                email: '',
            })
        })
    }

    render() {
        const { name, email } = this.state
        return (
            <Wrapper>
                <form>
                    <GlobalFonts />
                    <Title>We think your company would be a great fit!</Title>

                    <Label>Name</Label>
                    <InputText
                        type="text"
                        value={name}
                        onChange={this.handleChangeInputName}
                    />

                    <Label>Business Email </Label>
                    <InputText
                        type="text"
                        value={email}
                        onChange={this.handleChangeInputEmail}
                    />

                    <Button onClick={this.handleIncludeInfo}>Get Started Today</Button>
                    <Button>Show Submission</Button>
                </form>
            </Wrapper>
        )
    }
}

export default InfoInsert
