import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'
// import GlobalFonts from '../fonts/Poppins/fonts'

const Title = styled.h3`
    margin-bottom: 20px;
    align-self: center;
    font-weight: bold;
    font-family: Hachi Maru Pop;
`


const Label = styled.label`
`

const InputText = styled.input`

`

const Button = styled.button`
    margin-top: 20px;
    background: #484848;
    color: white;
    border: #484848;
    border-radius: 1px;
    padding: 10px 120px;
    transition-duration: 0.1s;
    &:hover {
        background: #36B1A7;
      }
`
const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 10px 20px;
    font-family: 'Popins';
    color: #484848;
    display: flex;
    flex-direction: column;
    justify-items: center;
    align-items: center;
`

const InfoForm = styled.form`
    display: flex;
    flex-direction: column;
    justify-items: center;
    max-width: fit-content;
`
const InfoList = styled.div`
    border: #484848;
    border-radius: 1px;
    color: black;
`


class InfoInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            email: '',
            info: null,
        }
    }

    handleChangeInputName = event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputEmail = event => {
        const email = event.target.value
        this.setState({ email })
    }

    handleIncludeInfo = async () => {
        const { name, email } = this.state
        const payload = { name, email }

        await api.createInfo(payload).then(res => {
            window.alert(`Info inserted successfully`)
            this.setState({
                name: '',
                email: '',
            })
        })
    }

    handleShowSubmission = async () => {
        await api.getInfo().then(res => {
            this.setState({ info: res.data })
        })
    }

    renderInfoList = () => {
        if(!info){
            return <InfoList>info</InfoList>
        }
    }

    render() {
        const { name, email, info } = this.state
        return (
            <Wrapper>
                <InfoForm>
                    {/* <GlobalFonts /> */}
                    <Title>We think your company would be a great fit!</Title>

                    <Label>Name</Label>
                    <InputText
                        type="text"
                        value={name}
                        onChange={this.handleChangeInputName}
                    />

                    <Label>Business Email </Label>
                    <InputText
                        type="text"
                        value={email}
                        onChange={this.handleChangeInputEmail}
                    />

                    <Button onClick={this.handleIncludeInfo}>Get Started Today</Button>
                    <Button onClick={this.handleShowSubmission}>Show Submission</Button>
                </InfoForm>

            </Wrapper>
        )
    }
}

export default InfoInsert
