import React, { Component } from 'react'
import api from '../api'

import styled from 'styled-components'

const Title = styled.h1.attrs({
    className: 'h1',

})``

const Wrapper = styled.div.attrs({
    className: 'form-group',
})`
    margin: 0 30px;
`

const Label = styled.label`
    margin: 5px;
`

const InputText = styled.input.attrs({
    className: 'form-control',
})`
    margin: 5px;
`

const Button = styled.button.attrs({
    className: `btn btn-primary`,
})`
    margin: 15px 15px 15px 5px;
`

const CancelButton = styled.a.attrs({
    className: `btn btn-danger`,
})`
    margin: 15px 15px 15px 5px;
`

class InfoInsert extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            email: '',
        }
    }

    handleChangeInputName = async event => {
        const name = event.target.value
        this.setState({ name })
    }

    handleChangeInputEmail = async event => {
        const email = event.target.value
        this.setState({ email })
    }

    handleIncludeInfo = async () => {
        const { name, email } = this.state
        const payload = { name, email }

        await api.insertMovie(payload).then(res => {
            window.alert(`Info inserted successfully`)
            this.setState({
                name: '',
                email: '',
            })
        })
    }

    render() {
        const { name, email } = this.state
        return (
            <Wrapper>
                <Title>We think your company would be a great fit!</Title>

                <Label>Name </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputName}
                />

                <Label>Business Email </Label>
                <InputText
                    type="text"
                    value={name}
                    onChange={this.handleChangeInputEmail}
                />

                <Button onClick={this.handleIncludeInfo}>Get Started Today</Button>
                <br/>
                <Button>Show Submission</Button>
            </Wrapper>
        )
    }
}

export default InfoInsert
